const msgerForm=get(".msger-inputarea"),msgerInput=get(".msger-input"),msgerChat=get(".msger-chat"),BOT_MSGS=["Hello sir, ","How can i help you ?","I’ll be there soon.","I am on the way","Oky..!! ","Thank you.. sir!!","Yes, I’ll arrive in 10 min"],BOT_IMG="../../assets/images/profile/p5.png",PERSON_IMG="../../assets/images/profile/p2.png",BOT_NAME="BOT",PERSON_NAME="Templeton Peck";function appendMessage(e,s,t,n){const r=`\n    <div class="msg ${t}-msg">\n      <div class="msg-img" style="background-image: url(${s})"></div>\n\n      <div class="msg-bubble">\n            <div class="msg-text title-color">${n}</div>\n      </div>\n    </div>\n  `;msgerChat.insertAdjacentHTML("beforeend",r),msgerChat.scrollTop+=500}function botResponse(){const e=random(0,BOT_MSGS.length-1),s=BOT_MSGS[e],t=100*s.split(" ").length;setTimeout(()=>{appendMessage("BOT",BOT_IMG,"left",s)},t)}function get(e,s=document){return s.querySelector(e)}function formatDate(e){const s="0"+e.getHours(),t="0"+e.getMinutes();return`${s.slice(-2)}:${t.slice(-2)}`}function random(e,s){return Math.floor(Math.random()*(s-e)+e)}msgerForm.addEventListener("submit",e=>{e.preventDefault();const s=msgerInput.value;s&&(appendMessage(PERSON_NAME,PERSON_IMG,"right",s),msgerInput.value="",botResponse())});