const progressBarClickHandler=e=>{progressPositionHandler(e)},sliderOnChangeHandler=()=>{const e=document.getElementById("slider"),t=document.getElementById("progress-bar");t.classList.add("remove-transition"),e.classList.add("remove-transition"),document.addEventListener("mousemove",progressPositionHandler),document.addEventListener("mouseup",function(){t.classList.remove("remove-transition"),e.classList.remove("remove-transition"),document.removeEventListener("mousemove",progressPositionHandler)})},progressPositionHandler=e=>{const t=document.getElementById("progress-bar-wrap"),n=document.getElementById("progress-bar"),s=document.getElementById("slider"),o=t.getBoundingClientRect();let r=(e.clientX-o.left)/o.width*100;e.clientX<o.left?r=0:e.clientX>o.right&&(r=100),n.style.width=r+"%",s.style.left=r+"%"};