const plusMinus=document.querySelectorAll(".plus-minus");for(var i=0;i<plusMinus.length;++i){const e=plusMinus[i].querySelector(".add"),u=plusMinus[i].querySelector(".sub");e?.addEventListener("click",function(){const e=this.parentNode.querySelector("input[type='number']");e.value<10&&(e.value=Number(e.value)+1)}),u?.addEventListener("click",function(){const e=this.parentNode.querySelector("input[type='number']");e.value>1&&(e.value=Number(e.value)-1)})}